import {AfterViewInit, Component, ElementRef, ViewChild} from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements AfterViewInit {

  scrolled: boolean = false;
  scrolledTop: boolean = false;

  @ViewChild('home') home!: ElementRef;

  ngAfterViewInit() {
    this.home.nativeElement.addEventListener('scroll', () => {
      let firstElPos = this.home.nativeElement.firstChild.getBoundingClientRect().top;
      firstElPos <= -10 ? this.scrolled = true : this.scrolled = false;
      firstElPos <= -window.innerHeight ? this.scrolledTop = true : this.scrolledTop = false;
    })
  }
}
<div class="home" #home>
  <app-navigation [scrolled]="scrolled"></app-navigation>
  <app-header></app-header>
  <img src="assets/images/home-header-bg.jpg" alt="background" />
  <app-home-sign-up></app-home-sign-up>
  <app-about-project></app-about-project>
  <app-scroll-to-top [scrolledTop]="scrolledTop"></app-scroll-to-top>
  <app-testimonials></app-testimonials>
  <app-authors></app-authors>
  <app-footer></app-footer>
</div>
