import {Component} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {ProjectService} from "../../../core/services/project.service";
import {IProject} from "../../../core/interfaces/iproject";
import {ProjectFacade} from "../../../facades/project-facade.service";
import {ActivatedRoute, Router} from "@angular/router";
import {ControlProjectsService} from "../../../core/services/control-projects.service";
import {Observable, of, pipe, Subject, switchMap, takeUntil, tap} from "rxjs";
import {Store} from "@ngrx/store";
import {createProject, loadProjects, ProjectStateModule, setProject, updateProject} from "../../../store";
import {stringify} from "postcss";
import {values} from "lodash";
import {MatSnackBar} from "@angular/material/snack-bar";
import {data} from "autoprefixer";
import {getProject} from "../../../store/rxProject/project.selectors";

@Component({
    selector: 'app-project-edit',
    templateUrl: './project-edit.component.html',
    styleUrls: ['./project-edit.component.scss']
})
export class ProjectEditComponent {
    disabled: boolean = true;
    touchUi: boolean = false;
    colorCtr: any;

    projects$ = this.projectFacade.myProjects$;
    sub$ = new Subject()
    projectId!: number;

    constructor(
        private store: Store<{ project: ProjectStateModule }>,
        private projectService: ProjectService,
        private projectFacade: ProjectFacade,
        private controlProjectsService: ControlProjectsService,
        private router: Router,
        private route: ActivatedRoute,
        private _snackBar: MatSnackBar,
    ) {
    }

    form: FormGroup = new FormGroup({
        id: new FormControl('null',),
        name: new FormControl('null',),
        description: new FormControl('null',),
        abbreviation: new FormControl('null',),
        color: new FormControl('null',),
    });
    color: any;
    project: IProject = {} as IProject;
    // currentProjectId: number = this.projectFacade.getProjectId();
    currentProjectId$: any = tap(() => {
        this.store.dispatch(loadProjects())
    })
    currentProj = this.projectService.getProjectById(this.currentProjectId$)
        .pipe(takeUntil(this.sub$))
        .subscribe((res) => {
            this.project = res;
            this.form.patchValue(this.project)

        })

    get currentProject(): any {
        // return this.projectFacade.getProject();
        return this.store.dispatch(loadProjects())
    }

    delete() {
        this.projectService.deleteProject(this.currentProjectId$)
            .pipe(takeUntil(this.sub$))
            .subscribe((res) => {
                localStorage.removeItem('project');
            });
        setTimeout(() => {
            location.reload()
        }, 2000)
    }

    ngOnInit(): void {
        this.route.params
            .pipe(takeUntil(this.sub$))
        switchMap((params:any) => {
            if (params['id']) {
                this.projectId = +params['id'];
                return this.store.select(getProject, {projectId: +params['id']})
            }
            return of(null)
        })

    }


    save() {
        this.form.markAllAsTouched();
        if (this.form.invalid) {
            return
        }
        if (this.projectId) {
            this.store.dispatch(updateProject({project: this.form.value}))
            this.router.navigate(['/application/setting/info']).then();
            return
        } else {
            this.store.dispatch(createProject({project: this.form.value}))
        }
    }


}

<h1>Edit Project</h1>
<div class="container">
<form [formGroup]="form" >
  <mat-form-field>
    <mat-label>Project Name</mat-label>
    <input matInput placeholder="Name" formControlName="name" >
  </mat-form-field>
  <mat-form-field>
    <mat-label>Abbreviation</mat-label>
    <input matInput placeholder="Abbreviation" formControlName="abbreviation" >
  </mat-form-field>
  <mat-form-field>
    <mat-label>Description</mat-label>
    <input matInput placeholder="Description" formControlName="description" >

  </mat-form-field>
  <mat-form-field>
    <mat-label>Color</mat-label>
    <input matInput [ngxMatColorPicker]="picker" [formControl]="colorCtr" formControlName="color">
    <ngx-mat-color-toggle matSuffix [for]="picker"></ngx-mat-color-toggle>
    <ngx-mat-color-picker #picker [touchUi]="touchUi" [color]="color"></ngx-mat-color-picker>
  </mat-form-field>
<button mat-raised-button color="primary" (click)="delete()">delete</button>
  <button mat-raised-button color="primary" (click)="save()">Save changes</button>
</form>
</div>
