import {Component, OnInit} from "@angular/core";
import {ControlProjectsService} from "../../../../core/services/control-projects.service";
import {BoardService} from "../../../../core/services/board.service";
import {Router} from "@angular/router";
import {IProject} from "../../../../core/interfaces/iproject";
import {PageEvent} from "@angular/material/paginator";
import {SharedService} from "../../../../core/services/shared.service";
import {ProjectFacade} from "../../../../facades/project-facade.service";

import {DescriptionComponent} from "./description.component";
import {Observable} from "rxjs";
import {Store} from "@ngrx/store";
import {ProjectStateModule} from "../../../../store";
import {currentProject} from "../../../../store/rxProject/project.selectors";


@Component({
  selector: 'app-project-info',
  templateUrl: 'project-info.component.html',
  styleUrls: ['./project-info.component.scss']
})

export class ProjectInfoComponent implements OnInit {

  constructor(private projectsService: ControlProjectsService,
              private boardService: BoardService,
              private sharedService: SharedService,
              private router: Router,
              // private projectFacade: ProjectFacade,
              private readonly store: Store<{project: ProjectStateModule}>,

  ) {
  }

  projects: IProject[] = [];
  projectsLength: number = 0;
  pageSize: number = 10;
  page: number = 1;

  currentProject$: Observable <IProject | null > = this.store.select(currentProject)

  /*  @Input('length') length!: number;
    @Input('pageSize') pageSize!: number;
    @Input('pageSizeOptions') pageSizeOptions!: number;*/
// get project(): IProject {
//   return this.projectFacade.getProject();
//
// }
  ngOnInit() {
    this.getProjects('DESC', this.page, this.pageSize);

    this.projectsService.getAllProjects()
      .subscribe(res => {
        this.projectsLength = res.length;

      })
  }

  getProjects(order: string, page: number, pageSize: number) {
    this.projectsService.getProjects(order, page, pageSize).subscribe({
      next: res => res.data.forEach((project: any) => {

        this.projects.push(project);
      }),
      error: err => console.log(err),
    })
  }


  settingsChanged(event: PageEvent) {
    console.log(event)
    this.page = event.pageIndex + 1;
    this.pageSize = event.pageSize;

    this.projects = [];
    this.getProjects('DESC', this.page, this.pageSize);
  }


}
<h1>Projects List</h1>
<ng-container *ngIf="currentProject$ | async as currentProject">
<div class="container">
  <div class="prj-container">
    <div class="project-container grid">
<!--      <div *ngFor="let project of projects; let i = index">-->
        <div class="inner-container" [ngStyle]="{backgroundColor: currentProject.color}" [routerLink]="['/application/setting/board',]
        // project.id
        ]">
          <div class="add-project">
            <p class="prj-title">project name: {{currentProject.name}}</p>
            <p class="prj-title">project abbreviation: {{currentProject.abbreviation}}</p>
            <p class="prj-title"><button [routerLink]="['/application/setting/info/description', currentProject.id]">Description</button></p>
            <button [routerLink]="['/application/setting/edit', currentProject.id]">Edit</button>
          </div>
        </div>
      </div>

    </div>

  </div>

<!--</div>-->
</ng-container>

<mat-paginator [length]="projectsLength"
               [pageSize]="pageSize"
               [pageSizeOptions]="[5, 10, 25, 100]"
               (page)="settingsChanged($event)"
               aria-label="Select page"
               class="paginator">
</mat-paginator>
