import {Component, Injectable} from '@angular/core';
import {MatDialogRef} from "@angular/material/dialog";
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {UsersService} from "../../../../../../core/services/users.service";

@Component({
  selector: 'app-user-edit',
  templateUrl: './user-edit.component.html',
  styleUrls: ['./user-edit.component.scss']
})

export class UserEditComponent {

  constructor(
    public dialogRef: MatDialogRef<UserEditComponent>,
   private usersService: UsersService,
  ) { }

  form: FormGroup = new FormGroup({
    id: new FormControl(null,),
    firstName: new FormControl(null, [Validators.required]),
    lastName: new FormControl(null, [Validators.required]),
    email: new FormControl(null, [Validators.required, Validators.email]),
    identityNumber: new FormControl(null, [Validators.required]),
    mobileNumber: new FormControl(null, [Validators.required]),
    isActive: new FormControl(true, [Validators.required]),
  });

  save() {
    this.form.markAllAsTouched()
    if (this.form.invalid) {
      return
    }
    this.usersService.createUser(this.form.value)
      .subscribe((res) => {
        this.dialogRef.close(res)
      })
  }


}
<div class="container">
  <h1>New User</h1>
  <div class="flex">
    <form [formGroup]="form">
      <mat-form-field>
        <mat-label>First Name</mat-label>
        <input matInput placeholder="First name" formControlName="firstName">
      </mat-form-field>
      <mat-form-field>
        <mat-label>Last Name</mat-label>
        <input matInput placeholder="Last name" formControlName="lastName">
      </mat-form-field>
      <mat-form-field>
        <mat-label>Identity Number</mat-label>
        <input matInput placeholder="Identity Number" formControlName="identityNumber">
      </mat-form-field>
      <mat-form-field>
        <mat-label>Email</mat-label>
        <input matInput placeholder="Email" formControlName="email">
      </mat-form-field>
      <mat-form-field>
        <mat-label>Mobile Number</mat-label>
        <input matInput placeholder="Mobile Number" formControlName="mobileNumber">
      </mat-form-field>
    </form>
  </div>
  <div mat-dialog-actions>
    <button mat-button (click)="dialogRef.close(false)">Cancel</button>
    <button mat-button (click)="save()">Save</button>
  </div>

</div>
