import {Router} from "@angular/router";
import {AuthService, ToggleSignupService} from "../../../core/services";
import {DOCUMENT} from "@angular/common";
import {fromEvent, Observable} from "rxjs";

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.scss']
})
export class NavigationComponent implements OnDestroy, OnInit {

  @Input('scrolled') scrolled!: boolean;

  constructor(
    public router: Router,
    private toggleSignup: ToggleSignupService,
    @Inject(DOCUMENT) private document: Document,
    public auth: AuthService
  ) {
  }

  ngOnInit() {
  }

  signUp() {
    this.toggleSignup.toggleVisible()
  }

  navigateToLogin() {
    this.router.navigate(['/auth']);
  }

  menuIsOpened: boolean = false;

  clicks$: Observable<Event> = fromEvent(this.document, 'click');
  clicksSub: any;

  toggleMenu() {
    if(!this.menuIsOpened) {
      this.clicksSub = this.clicks$.subscribe( (event: any) => {  // ???
        if (!event.target.classList.contains('dont-close-from-outside')) {
          this.menuIsOpened = false;
          this.clicksSub.unsubscribe();
        }
      });
    }

    this.menuIsOpened = !this.menuIsOpened
  }

  closeMenu(event: any) {
    if (event.target.tagName === 'A' || event.target.tagName === 'BUTTON' && event.currentTarget.classList.contains('visible')) {
      this.menuIsOpened = false;
    }
  }

  ngOnDestroy() {
    if(this.menuIsOpened) {
      this.clicksSub.unsubscribe();
    }
  }
}
<div class="nav" [ngClass]="scrolled ? 'scrolled' : ''">
  <a href="#header" (click)="menuIsOpened = false; router.navigate(['/#header'])" class="dont-close-from-outside">
    <img src="assets/image/logo.svg" alt="logo" class="logo"/>
  </a>
  <div class="nav-links dont-close-from-outside" [ngClass]="menuIsOpened ? 'visible' : ''" (click)="closeMenu($event)">
    <div></div>
    <a href="#header" class="dont-close-from-outside"> home</a>
    <a href="#about" class="dont-close-from-outside"> about project</a>
    <a href="#testimonials" class="dont-close-from-outside"> testimonials</a>
    <a href="#authors" class="nav-link"> authors</a>
<!--    <a routerLink="/dashboard">Dashboard</a>-->
<!--    <a routerLink="..//application/setting/info" class="nav-link" *ngIf="auth.getToken()"> Task Manager</a>-->
    <section class="auth-links" *ngIf="!auth.getToken()">
      <button type="button" (click)="signUp()" class="dont-close-from-outside">sign up</button>
      <button type="button" (click)="navigateToLogin()" style="color: aliceblue">log in</button>
    </section>
    <span *ngIf="auth.getToken()">
      <p>{{auth.getUser().firstName}} {{auth.getUser().lastName}}</p>
      <h4 (click)="auth.logout()">Log Out</h4>
    </span>
  </div>
  <div class="auth-links" *ngIf="!auth.getToken(); else userInfo">
    <button type="button" (click)="signUp()">sign up</button>
    <button type="button" (click)="navigateToLogin()">Log in</button>

  </div>
  <div class="toggle-nav dont-close-from-outside" [ngClass]="menuIsOpened ? 'opened' : ''" (click)="toggleMenu()">
    <div></div>
    <div></div>
    <div></div>
  </div>
</div>

<ng-template #userInfo>
  <div class="user-info">
    <div>
      <p>{{auth.getUser().email}}</p>
    </div>
    <div>
      <span>
        <p>{{auth.getUser().firstName}}</p>
        <p>{{auth.getUser().lastName}}</p>
      </span>
      <p routerLink="../application/setting/info">Task Manager</p>
      <h4 (click)="auth.logout()">Log Out</h4>
    </div>
  </div>
</ng-template>
