import {Component} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";
import {CheckMailService, ToggleSignupService} from "../../../core/services";

@Component({
  selector: 'app-home-sign-up',
  templateUrl: './home-sign-up.component.html',
  styleUrls: ['./home-sign-up.component.scss']
})
export class HomeSignUpComponent {

  constructor(
    private router: Router,
    public toggleSignup: ToggleSignupService,
    private checkMail: CheckMailService
  ) { }

  email: FormGroup = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email] )
  })

  touched: boolean = false;
  exists?: boolean;
  diameter: number = 30;
  spinner: boolean = false;

  checkEmail(email: string) {
    this.checkMail.checkMail({email: email}).subscribe(res => {
      res.exists ? this.router.navigate(['../auth/login'], {queryParams: {email: email}}) : this.router.navigate(['../auth/register'], {queryParams: {email: email}})
    })
  }

  onSubmit() {
    this.touched = true;
    this.email.get('email')?.markAsTouched();

    setTimeout(()=>{
      this.touched = false;
      this.email.get('email')?.markAsUntouched();
    }, 4000)

    let email = this.email.get('email')?.value

    if(this.email.valid) {
      this.spinner = true;
      this.checkEmail(email);
    }
  }

  focus() {
    this.touched = false;
  }

}
<div class="home-page-signup" [ngClass]="toggleSignup.visible ? 'visible' : ''">
  <div class="blur"></div>
  <img class="bg" src="assets/images/mail-cather__bg.svg">
  <div class="container">
    <span>
      <h1>
        Sign up for FREE
      </h1><br>
      <p>
        and start using Task Manager in seconds!
      </p>
    </span>
    <form  (ngSubmit)="onSubmit()" [formGroup]="email">
      <input type="email" placeholder="Enter your work email" formControlName="email">
      <button type="submit">
        <span *ngIf="!spinner">Get Started</span>
        <mat-spinner class="spinner" *ngIf="spinner" [diameter]="diameter" color="accent"></mat-spinner>
      </button>
      <p style="color: white; margin: 10px 0 -28px 0; font-size: 1rem;"
         *ngIf="touched && email.get('email')?.invalid && email.get('email')?.touched && !email.get('email')?.getError('email')">
        * Email is required!
      </p>
      <p style="color: white; margin: 10px 0 -28px 0; font-size: 1rem"
         *ngIf="touched && email.get('email')?.getError('email') && email.get('email')?.touched">
        * Please enter valid email!
      </p>
    </form>
  </div>
  <img class="close" src="assets/images/x-mark.png" (click)="toggleSignup.toggleVisible()">
</div>
