import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import { Router} from "@angular/router";
import {CheckMailService} from "../../../core/services";


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {


  constructor(
    private router: Router,
    private checkMail: CheckMailService
  ) { }



  ngOnInit(): void {
  }

  spinner: boolean = false;
  diameter: number = 30;


  email: FormGroup = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email] )
  })

  touched: boolean = false;
  exists?: boolean;

  checkEmail(email: string) {
    this.checkMail.checkMail({email: email}).subscribe(res => {
      res.exists ? this.router.navigate(['../auth/login'], {queryParams: {email: email}}) : this.router.navigate(['../auth/register'], {queryParams: {email: email}})
    })
  }

  submit() {
    this.touched = true;
    this.email.get('email')?.markAsTouched();

    setTimeout(()=>{
      this.touched = false;
      this.email.get('email')?.markAsUntouched();
    }, 4000)

    let email = this.email.get('email')?.value

    if(this.email.valid) {
      this.spinner = true;
      this.checkEmail(email);
    }
  }

  focus() {
    this.touched = false;
  }

}
<div class="header" id="header">
  <div class="container" [formGroup]="email">
    <h1>
      One app to <br> manage all Projects.
    </h1>
    <p>
      All of your work in one place
    </p>
    <input type="email" placeholder="Enter your work email" formControlName="email" (focus)="focus()"  (keyup.enter)="submit()">
    <p style="color: red; margin: -10px 0 20px 0; font-size: 1rem;"
       *ngIf="touched && email.get('email')?.invalid && email.get('email')?.touched && !email.get('email')?.getError('email')">
      * Email is required!
    </p>
    <p style="color: red; margin: -10px 0 20px 0; font-size: 1rem"
       *ngIf="touched && email.get('email')?.getError('email') && email.get('email')?.touched">
      * Please enter valid email!
    </p>
    <div>
      <button type="button" (click)="submit()">
        <span *ngIf="!spinner">Get Started</span>
        <mat-spinner class="spinner" *ngIf="spinner" [diameter]="diameter" color="accent"></mat-spinner>
      </button>
      <span>
        <span>free forever.</span>
        <span>no credit card.</span>
      </span>
    </div>
  </div>
  <img src="assets/images/undraw_scrum_board_re_wk7v.svg">
</div>

