import {createReducer, on} from "@ngrx/store";
import {loadProjects, loadProjectsFailure, loadProjectsSuccess, setProject} from "./project.actions";
import {IProject} from "../../core/interfaces/iproject";

export interface ProjectState {
    projects: IProject[];
    currentProject: IProject | null;
    error: any;
}
const initialState = {
    projects: [],
    currentProject: null,
};
export const projectReducer = createReducer(
    initialState,
    on(loadProjects, state => state),
    on(loadProjectsSuccess, (state, action) => {
        return {
            ...state,
            projects: action.data
        };
    }
    ),
    on(loadProjectsFailure, (state, action) => state),
    on(setProject, (state, action) => {
        const project = state.projects.find((project) => project.id === action.projectId);
        return {
            ...state,
            currentProject: action.projectId
        };
        }
    );
