import {AfterViewInit, Component, ElementRef, ViewChild} from '@angular/core';
import {fromEvent, Observable} from "rxjs";
import {Router} from "@angular/router";

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss']
})
export class FooterComponent implements AfterViewInit {
  @ViewChild('footer') footer!: ElementRef;

  constructor(public router: Router) {
  }

  foot?: any;

  ngAfterViewInit() {
    this.foot = this.footer.nativeElement;

    let footer$: Observable<any> = fromEvent(this.foot, "mousemove");

    footer$.subscribe(event => {
      this.onMouseMove(event)
    })
  }

  onMouseMove(event: any) {
    let percentage = Math.floor(event.clientX / window.innerWidth * 100);

    event.currentTarget.style.background = `
    linear-gradient(97deg, rgba(207,60,190,1) 0%,
    rgba(133,33,243,1) ${percentage}%,
    rgba(207,60,190,1) 100%)`;
  }


}
<footer #footer>
  <div class="items-count">
    <span>
      <h1>2569</h1>
      <h1>Users</h1>
    </span>
    <span>
      <h1>45873</h1>
      <h1>Projects</h1>
    </span>
    <span>
      <h1>758641</h1>
      <h1>Tasks</h1>
    </span>
  </div>
  <div class="content">
    <div>
      <h1>Try it for Free</h1>
      <button (click)="this.router.navigate(['/register'])" type="button">
        <div></div>
        <p>Register Now</p>
      </button>
    </div>
  </div>
  <div class="terms">
    <span>
      <h1><a class="logo" href="#header">
        <img src="assets/image/logo.svg" class="logo" >
      </a></h1>
      <p>© 2023 | All rights reserved | Terms</p>
    </span>
    <a href="" target="_blank">
      <img src="assets/images/github-character.png" alt="github">
    </a>
  </div>
</footer>
