import {Component, OnInit} from '@angular/core';
import {FormArray, FormControl, FormGroup, Validators} from "@angular/forms";
import {IssueTypeEnum} from "../../../../../core/enums";
import {IssueTypesService} from "../../../../../core/services";
import {ActivatedRoute, Router} from "@angular/router";

@Component({
  selector: 'app-issue-type-add-edit',
  templateUrl: './issue-types-add-edit.component.html',
  styleUrls: ['./issue-types-add-edit.component.scss']
})
export class IssueTypesAddEditComponent implements OnInit {
  form: FormGroup = new FormGroup({
    id: new FormControl(null),
    name: new FormControl(null, Validators.required),
    icon: new FormControl(null, Validators.required),
    color: new FormControl(1, Validators.required),
    type: new FormControl(null, Validators.required),
    issueTypeColumns: new FormArray([], Validators.required),
  })
  issueTypes = Object.values(IssueTypeEnum);

  issueTypeId!: number;

  get columnsFormArray() {
    return this.form.get('issueTypeColumns') as FormArray;
  }

  constructor(
    private readonly issueTypeService: IssueTypesService,
    private router: Router,
    private route: ActivatedRoute
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      if (params['id']) {
        this.issueTypeId = +params['id'];
        this.getBoards()
      }
    })
  }

  getBoards() {
    this.issueTypeService.getIssueTypesByID(this.issueTypeId).subscribe(data => {
      this.form.patchValue(data)
      data.issueTypeColumns.forEach((column: any) => {
        this.columnsFormArray.push(new FormGroup({
          id: new FormControl(column.id),
          name: new FormControl(column.name, Validators.required),
          filedName: new FormControl(column.filedName, Validators.required),
          isRequired: new FormControl(column.isRequired, Validators.required)
        }, Validators.required));
      })
    })
  }

  addColumn() {
    this.columnsFormArray.push(new FormGroup({
      name: new FormControl(null, Validators.required),
      filedName: new FormControl(null, Validators.required),
      isRequired: new FormControl(false, Validators.required),
    }, Validators.required));
  }


  saveAs() {

    this.form.markAllAsTouched()
    // if (this.form.invalid) {
    //   return;
    // }
    if (this.issueTypeId) {
      this.issueTypeService.updateIssueType(this.form.value)
        .subscribe( res => {
          this.router.navigate(['/application/setting/issueType']).then()
        })
    } else {
      this.issueTypeService.setIssueType(this.form.value)
        .subscribe( res => {
          this.router.navigate(['/application/setting/issueType']).then()
          console.log(this.form.value)
        })
    }


  }

  removeColumn(i: number) {
    this.columnsFormArray.removeAt(i);
  }
}
<div>
  <form [formGroup]="form">
    <div class="form-container">
      <mat-form-field>
        <mat-label>Name</mat-label>
        <input matInput placeholder="Name" formControlName="name">
      </mat-form-field>
<!--      <mat-form-field>-->
<!--        <mat-label>Description</mat-label>-->
<!--        <input matInput placeholder="Description" formControlName="description">-->
<!--      </mat-form-field>-->

      <mat-form-field>
        <mat-label>Issue Type</mat-label>
        <mat-select formControlName="type" placeholder="Issue Type">
          <mat-option *ngFor="let issueType of issueTypes" [value]="issueType" >
            {{issueType}}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field>
        <mat-label>Color</mat-label>
        <input matInput placeholder="Color" formControlName="color" type="color">
      </mat-form-field>

      <mat-form-field>
        <mat-label>Icon</mat-label>
        <input matInput placeholder="Icon" formControlName="icon">
      </mat-form-field>

      <div class="btns">
        <button mat-raised-button color="primary" (click)="addColumn()">Add Field</button>
        <button mat-raised-button color="primary" (click)="saveAs()">Save</button>
      </div>

    </div>

    <div class="columns" formArrayName="issueTypeColumns">
      <div *ngFor="let column of columnsFormArray.controls; let i = index" >
        <div class="flex" [formGroupName]="i">
          <mat-form-field>
            <mat-label>Name</mat-label>
            <input matInput placeholder="Name" formControlName="name">
          </mat-form-field>
          <mat-form-field>
            <mat-label>Field Name</mat-label>
            <input matInput placeholder="Description" formControlName="filedName">
          </mat-form-field>

          <mat-checkbox class="example-margin" formControlName="isRequired">Is required</mat-checkbox>

          <button mat-raised-button color="primary" (click)="removeColumn(i)">Remove Column</button>
        </div>
      </div>
    </div>

  </form>
</div>
