import {Component, OnInit} from '@angular/core';
import {MatTableDataSource} from "@angular/material/table";
import {of, Subject, switchMap, takeUntil} from "rxjs";
import {User} from "../../../../core/interfaces";
import {UsersService} from "../../../../core/services/users.service";
import {MatDialog} from "@angular/material/dialog";
import {ConfirmDeleteComponent} from "../../../../shared/confirm-delete/confirm-delete.component";
import {PageEvent} from "@angular/material/paginator";
import {UserRoleComponent} from "../user-role/user-role.component";
import {UserAddEditComponent} from "../user-add-edit/user-add-edit.component";
import {Store} from "@ngrx/store";
import {loadUsers, UserStateModel} from "../../store ";


@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss']
})
export class UsersComponent implements OnInit{
  displayedColumns = ['id', 'fullName', 'createdAt', 'actions'];

  dataSource = new MatTableDataSource<User>();

  sub$ = new Subject();
  pageIndex  = 1;
  total = 0;
  pageSize = 10;

  constructor(
      private store: Store<{user: UserStateModel}>,
    private userService: UsersService,
    public dialog: MatDialog
  ) {
  }

  ngOnInit() {
    this.store.dispatch()
    this.getUsers();
  }

  getUsers() {
    this.userService.getUsers({
      page: this.pageIndex,
      limit: this.pageSize
    })
      .subscribe(users => {
        this.dataSource.data = users.data;

        this.total = users.totalCount;
      });
  }


  addUser(id?: number) {
    const dialogRef = this.dialog.open(UserAddEditComponent, {
      data: {
        userId: id
      }
    });


    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.getUsers();
      }
    })

  }

  delete(id: number) {
    const dialogRef = this.dialog.open(ConfirmDeleteComponent);

    dialogRef.afterClosed()
      .pipe(
        takeUntil(this.sub$),
        switchMap((result) => {
          if (result) {
            return this.userService.deleteUserById(id);
          }
          return of(null);
        })
      )
      .subscribe(result => {
        if (result) {
          this.getUsers();
        }
      });
  }

  pageEvent($event: PageEvent) {
    this.pageIndex = $event.pageIndex + 1;
    this.pageSize = $event.pageSize;
    this.getUsers()
  }

  setRole(user: User) {
    const dialogRef = this.dialog.open(UserRoleComponent, {
      data: {
        user: user,
      }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.getUsers();
      }
    })
  }
}

<div>
  <button mat-raised-button color="primary" (click)="addUser()" [appPermissions]="['user:create']">Add User</button>
</div>

<div class="container">
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

    <!-- Name Column -->
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef> # </th>
      <td mat-cell *matCellDef="let element"> {{element.id}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="fullName">
      <th mat-header-cell *matHeaderCellDef> fullName </th>
      <td mat-cell *matCellDef="let element"> {{element.firstName}} {{element.lastName}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="createdAt">
      <th mat-header-cell *matHeaderCellDef> Created At </th>
      <td mat-cell *matCellDef="let element"> {{element.createdAt}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef>  </th>
      <td mat-cell *matCellDef="let element">
        <button mat-raised-button color="primary" (click)="setRole(element)">Set Role</button>
        <button mat-raised-button color="primary" (click)="addUser(element.id)">Edit</button>
        <button mat-raised-button color="warn" (click)="delete(element.id)">Delete</button>
      </td>
    </ng-container>


    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>

  <mat-paginator [length]="total"
                 [pageSize]="pageSize"
                 [pageIndex]="pageIndex"
                 [pageSizeOptions]="[5, 10, 25, 100]"
                 (page)="pageEvent($event)"
                 aria-label="Select page">
  </mat-paginator>
</div>
