import {Component, OnDestroy, OnInit} from '@angular/core';
import {BoardService} from "../../../../core/services/board.service";
import {Observable, of, pipe, Subject, switchMap, takeUntil} from "rxjs";
import {IBoard} from "../../../../core/interfaces";

import {MatTableDataSource} from "@angular/material/table";
import {MatDialog} from "@angular/material/dialog";
import {ConfirmDeleteComponent} from "../../../../shared/confirm-delete/confirm-delete.component";
import {Store} from "@ngrx/store";
import {ProjectStateModule} from "../../../../store";
import {currentProject} from "../../../../store/rxProject/project.selectors";


@Component({
    selector: 'app-project-board',
    templateUrl: './project-board.component.html',
    styleUrls: ['./project-board.component.scss']
})
export class ProjectBoardComponent implements OnInit, OnDestroy {

    displayedColumns = ['id', 'name', 'createdAt', 'actions']
    dataSource = new MatTableDataSource<IBoard>();
    sub$ = new Subject()

    constructor(
        private readonly store: Store<{ project: ProjectStateModule }>,
        private boardService: BoardService,
        private dialog: MatDialog,
        // private route: ActivatedRoute,
    ) {

    }

    getBoard() {
        this.boardService.getProjBoards().subscribe(boards => {
            this.dataSource.data = boards
        })

    }

    ngOnInit(): void {
        this.store.select(currentProject)
            .
        this.getBoard()
        // this.route.params
        //   .pipe(
        //     takeUntil(this.sub$)
        //   )
        //   .subscribe(params => {
        //   if (params['id']) {
        //     this.boardService.getBoardss(params['id']).subscribe({
        //       next: res => this.boards = res
        //     })
        //     console.log(params['id'])
        //   }
        // })
    }

    ngOnDestroy(): void {
        this.sub$.next(null);
        this.sub$.complete();
    }

    deleteBoard(id: number) {
        const dialogRef = this.dialog.open(ConfirmDeleteComponent);
        dialogRef.afterClosed()
            .pipe(
                takeUntil(this.sub$),
                switchMap((result) => {
                    if (result) {
                        console.log(result)
                        return this.boardService.deleteBoard(id);
                    }
                    return of(null);
                })
            ).subscribe(result => {
            if (result) {
                this.getBoard();
            }
        });
    }


}


<div class="cont">
  <!--    <ng-container *ngIf="boards$ | async as boards" ></ng-container>-->
  <div>
    <!--  <mat-select class="select">-->
    <!--    <mat-option *ngFor="let board of boards" [value]="board.id" >{{board.name}}</mat-option>-->
    <!--  </mat-select>-->
    <button mat-raised-button color="primary"

            [routerLink]="['/application/setting/board/addBoard']"
    >Add Board
    </button>

  </div>
</div>

<div class="container">
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

    <!-- Position Column -->
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef> Id</th>
      <td mat-cell *matCellDef="let element"> {{element.id}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef> Name</th>
      <td mat-cell *matCellDef="let element"> {{element.name}} </td>
    </ng-container>

    <!-- Weight Column -->
    <ng-container matColumnDef="createdAt">
      <th mat-header-cell *matHeaderCellDef> Created At</th>
      <td mat-cell *matCellDef="let element"> {{element.createdAt}} </td>
    </ng-container>

    <!-- Symbol Column -->
    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef></th>
      <td mat-cell *matCellDef="let element">
        <button mat-raised-button color="primary" [routerLink]="['/application/setting/board/editBoard/', element.id]">
          Edit
        </button>
        <button mat-raised-button color="warn" (click)="deleteBoard(element.id)">Delete</button>

      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>

</div>
