import {Component, OnDestroy, OnInit} from '@angular/core';
import {MatTableDataSource} from "@angular/material/table";
import {of, Subject, switchMap, takeUntil} from "rxjs";
import {IssueTypesService} from "../../../../core/services";
import {MatDialog} from "@angular/material/dialog";
import {ConfirmDeleteComponent} from "../../../../shared/confirm-delete/confirm-delete.component";
import {IssueType} from "../../../../core/interfaces";
import {Store} from "@ngrx/store";
import {ProjectStateModule} from "../../../../store";
import {currentProject} from "../../../../store/rxProject/project.selectors";

@Component({
    selector: 'app-project-issue-type',
    templateUrl: './project-issue-types.component.html',
    styleUrls: ['./project-issue-types.component.scss']
})
export class ProjectIssueTypesComponent implements OnInit, OnDestroy {
    displayedColumns = ['id', 'name', 'createdAt', 'actions'];

    dataSource = new MatTableDataSource<IssueType>();

    sub$ = new Subject();

    constructor(
        private store: Store<{project: ProjectStateModule}>,
        private issueTypesService: IssueTypesService,
        public dialog: MatDialog,
        private issueTypeService: IssueTypesService) {
    }

    ngOnInit() {
        // this.getIssueTypes();
        this.store.select(currentProject)
            .subscribe((project)=>{
                if (project){
                    this.getIssueTypes();
                }
            })
    }

    getIssueTypes() {
        this.issueTypesService.getIssueType()
            .pipe(takeUntil(this.sub$))
            .subscribe(boards => {
                console.log(boards)
                this.dataSource.data = boards;
            });
    }

    deleteIssueType(id: number) {
        const dialogRef = this.dialog.open(ConfirmDeleteComponent);

        dialogRef.afterClosed()
            .pipe(
                takeUntil(this.sub$),
                switchMap((result) => {
                    if (result) {
                        return this.issueTypeService.deleteIssueType(id);
                    }
                    return of(null);
                })
            )
            .subscribe(result => {
                if (result) {
                    this.getIssueTypes();
                }
            });
    }

    ngOnDestroy(): void {
        this.sub$.next(null);
        this.sub$.complete();
    }
}


<div>
  <button mat-raised-button color="primary" [routerLink]="['/application/setting/issue-types/add']">Add Issue Type</button>
</div>


<div class="container">
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

    <!-- Name Column -->
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef> # </th>
      <td mat-cell *matCellDef="let element"> {{element.id}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef> Name </th>
      <td mat-cell *matCellDef="let element"> {{element.name}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="createdAt">
      <th mat-header-cell *matHeaderCellDef> Created At </th>
      <td mat-cell *matCellDef="let element"> {{element.createdAt}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef>  </th>
      <td mat-cell *matCellDef="let element">
        <button mat-raised-button color="primary" [routerLink]="['/application/setting/issue-types/edit', element.id]">Edit</button>
        <button mat-raised-button color="warn" (click)="deleteIssueType(element.id)">Delete</button>
      </td>
    </ng-container>


    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
</div>
