import {Component, ViewChild} from '@angular/core';
import {MatDrawer} from "@angular/material/sidenav";
import {DrawerService} from "../../../core/services/drawer.service";
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-project-setting',
  templateUrl: './project-setting.component.html',
  styleUrls: ['./project-setting.component.scss']
})
export class ProjectSettingComponent {

  constructor(private drawerService: DrawerService,
              private route: ActivatedRoute) {
  }

  isOpen = false;
  @ViewChild(MatDrawer) drawer!: MatDrawer;

  ngOnInit() {
    this.drawerService.isDrawerOpen$.subscribe((res: boolean) => {
      if (res) {
        this.drawer?.open();
      } else {
        this.drawer?.close();
      }
    });

    this.route.params.subscribe(params => {
      if(params['id']) {

      }
    })
  }

  toggleDrawer() {
    if (!this.isOpen) {
      this.drawerService.openDrawer();
      this.isOpen = true;
    } else if (this.isOpen) {
      this.drawerService.closeDrawer();
      this.isOpen = false;
    }
  }
}

<mat-drawer-container class="mat-container" autosize>
  <mat-drawer #drawer class="sidenav" position="start" mode="side">
    <!--    <span class="material-icons close-btn" (click)="closeDrawer()">close</span>-->
    <ul>
      <li>
        <a routerLink="/main/projects">Projects</a>
      </li>
      <li>
        <a routerLink="/projects/setting/epics">Epics</a>
      </li>
      <li>
        <a routerLink="/projects/setting/issue-types">Issue Types</a>
      </li>
      <li>
        <a routerLink="">Home</a>
      </li>
    </ul>


  </mat-drawer>

  <div class="sidenav-content">
    <button type="button" (click)="toggleDrawer()" class="toggle-sidenav">
      <span class="material-icons" *ngIf="!isOpen">chevron_right</span>
      <span class="material-icons" *ngIf="isOpen">chevron_left</span>
    </button>
    <router-outlet></router-outlet>
  </div>

</mat-drawer-container>
