import {createAction, props} from "@ngrx/store";

export  const loadUsers = createAction(
    '[User] Load Users',
        props<{
        page: number,
        limit: number,
        search: string
    }>()
);

export  const loadUsersSuccess = createAction(
    '[User] Load Users Success',
    props<{ users: any }>()
);
const loadUsersFailure = createAction(
    '[User] Load Users Failure',
    props<{ error: any }>()
);
