import {Injectable} from "@angular/core";
import {Actions, createEffect, ofType} from "@ngrx/effects";
import {ProjectService} from "../../core/services/project.service";
import {loadProjects, loadProjectsFailure, loadProjectsSuccess, setProject} from "./project.actions";
import {catchError, of, switchMap, map} from "rxjs";
import {Store} from "@ngrx/store";
import {ProjectState} from "./project.reducer";


@Injectable()
export class ProjectEffects{
    constructor(
        private actions$: Actions,
        private projectService: ProjectService,
        private store: Store,
    ) {
    }

loadProjects$ = createEffect(() => this.actions$.pipe(
    ofType(loadProjects),
    switchMap(() => this.projectService.getMyProjects()
        .pipe(
            map((data: any) => loadProjectsSuccess({data})),
            catchError((error) => of(loadProjectsFailure({error})))
        ))
    ));

    setProject$ = createEffect(() => this.actions$.pipe(
        ofType(setProject),
        switchMap((action) => {
            this.store.dispatch(loadProjectsSuccess({data: action.data}))
        }
        )
    ))


}
