import {Component, OnInit} from '@angular/core';
import {MatTableDataSource} from "@angular/material/table";
import {of, pipe, Subject, switchMap, takeUntil} from "rxjs";
import {EpicService} from "../../../../core/services/epic.service";
import {MatDialog} from "@angular/material/dialog";
import {IEpic} from "../../../../core/interfaces/epic";
import {ConfirmDeleteComponent} from "../../../../shared/confirm-delete/confirm-delete.component";
import {Store} from "@ngrx/store";
import {ProjectStateModule} from "../../../../store";
import {currentProject} from "../../../../store/rxProject/project.selectors";

@Component({
    selector: 'app-project-epic',
    templateUrl: './project-epics.component.html',
    styleUrls: ['./project-epics.component.scss']
})
export class ProjectEpicsComponent implements OnInit {
    displayedColumns = ['id', 'name', 'createdAt', 'actions'];

    dataSource = new MatTableDataSource<IEpic>();

    sub$ = new Subject();

    constructor(
        private store: Store<{ project: ProjectStateModule }>,
        private epicService: EpicService,
        public matDialog: MatDialog,
    ) {
    }

    ngOnInit(): void {
        // this.getEpics();
        this.store.select(currentProject)
            .subscribe((project)=>{
                if (project){
                    this.getEpics();;
                }
            })
    }


    getEpics() {
        this.epicService.getProjEpics()
            .pipe(takeUntil(this.sub$))
            .subscribe(epics => {
                this.dataSource.data = epics;
            })
    }

    deleteEpic(id: number) {
        const dialogRef = this.matDialog.open(ConfirmDeleteComponent);
        dialogRef.afterClosed()
            .pipe(
                takeUntil(this.sub$),
                switchMap((result) => {
                    if (result) {
                        return this.epicService.deleteProjEpic(id);
                    }
                    return of(null);
                })
            ).pipe(takeUntil(this.sub$))
            .subscribe(result => {
                if (result) {
                    this.getEpics();
                }
            })
    }

    ngOnDestroy(): void {
        this.sub$.next(null);
        this.sub$.complete();
    }

}
<div class="container">
<div>
  <button mat-raised-button color="primary" [routerLink]="['/application/setting/epics/add']">Add Epic</button>
</div>


<div >
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

    <!-- Name Column -->
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef> # </th>
      <td mat-cell *matCellDef="let element"> {{element.id}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef> Name </th>
      <td mat-cell *matCellDef="let element"> {{element.name}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="createdAt">
      <th mat-header-cell *matHeaderCellDef> Created At </th>
      <td mat-cell *matCellDef="let element"> {{element.createdAt}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef>  </th>
      <td mat-cell *matCellDef="let element">
        <button mat-raised-button color="primary" [routerLink]="['/projects/setting/epics/edit', element.id]">Edit</button>
        <button mat-raised-button color="warn" (click)="deleteEpic(element.id)">Delete</button>
      </td>
    </ng-container>


    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
</div>
</div>
